import { AppBar, Toolbar, Typography, Avatar, Menu, MenuItem, Button, Box } from "@mui/material";
import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);

  if (!user) return null;

  const handleMenuOpen = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);

  return (
    <AppBar
      position="static"
      sx={{
        background: 'linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)', // unique gradient
        boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
        borderBottom: '1px solid rgba(255,255,255,0.2)',
      }}
    >
      <Toolbar>
        {/* Dashboard title */}
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 600 }}>
          Knit Finance Dashboard
        </Typography>

        {/* Profile Avatar */}
        <Box>
          <Avatar
            sx={{
              background: 'linear-gradient(135deg, #43cea2 0%, #185a9d 100%)', // gradient // aesthetic soft blue
              width: 40,
              height: 40,
              fontWeight: 600,
              cursor: 'pointer',
              color: '#fff',
            }}
            onClick={handleMenuOpen}
          >
            {user.name[0].toUpperCase()}
          </Avatar>

          {/* Dropdown Menu */}
          <Menu
            anchorEl={anchorEl}
            open={open}
            onClose={handleMenuClose}
            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            transformOrigin={{ vertical: 'top', horizontal: 'right' }}
            sx={{ mt: 1 }}
          >
            <Box sx={{ px: 2, py: 1, minWidth: 200 }}>
              <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                {user.name}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {user.email}
              </Typography>
            </Box>
            <MenuItem>
              <Button
                fullWidth
                variant="outlined"
                color="primary" // can customize color
                onClick={() => { logout(); handleMenuClose(); }}
              >
                Logout
              </Button>
            </MenuItem>
          </Menu>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
