import { useEffect, useState } from "react";
import { AppBar, Toolbar, Typography, Avatar, Box, Button, Menu, MenuItem } from "@mui/material";
import API from "../services/api";

const Navbar = ({ logout }) => {
  const [profile, setProfile] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await API.get("/profile");
        setProfile(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProfile();
  }, []);

  const handleMenu = (event) => setAnchorEl(event.currentTarget);
  const handleClose = () => setAnchorEl(null);

  return (
    <AppBar position="static">
      <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
        <Typography variant="h6">My App</Typography>
        {profile && (
          <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
            <Avatar src={profile.avatar} alt={profile.name} onClick={handleMenu} sx={{ cursor: "pointer" }} />
            <Typography>{profile.name}</Typography>
            <Button color="inherit" onClick={logout}>Logout</Button>

            <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
              <MenuItem>{profile.name}</MenuItem>
              <MenuItem>{profile.email}</MenuItem>
            </Menu>
          </Box>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
