import { useState, useContext } from "react";
import {
  TextField,
  Button,
  Box,
  Typography,
  Paper,
  IconButton,
  InputAdornment,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { Link } from "react-router-dom";
import API from "../services/api";
import { AuthContext } from "../context/AuthContext";

const Login = () => {
  const { login } = useContext(AuthContext);
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post("/auth/login", form);
      login(res.data.user, res.data.token);
    } catch {
      setError("Invalid credentials");
    }
  };

  return (
    <Box
      sx={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#98C6E8", // page background
        p: 2,
      }}
    >
      <Paper
        elevation={8}
        sx={{
          backdropFilter: "blur(12px)",
          background: "rgba(255, 255, 255, 0.25)", // glassmorphism card
          borderRadius: 4,
          p: 5,
          width: "100%",
          maxWidth: 400,
        }}
      >
        <Typography
          variant="h5"
          sx={{ textAlign: "center", mb: 3, color: "#073763", fontWeight: 600 }}
        >
          Login
        </Typography>

        {error && (
          <Typography color="error" mt={1} sx={{ textAlign: "center" }}>
            {error}
          </Typography>
        )}

        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            variant="outlined"
            label="Email"
            margin="normal"
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            InputLabelProps={{ style: { color: "#073763" } }}
            InputProps={{ style: { color: "#073763" } }}
          />

          <TextField
            fullWidth
            variant="outlined"
            label="Password"
            type={showPassword ? "text" : "password"}
            margin="normal"
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            InputLabelProps={{ style: { color: "#073763" } }}
            InputProps={{
              style: { color: "#073763" },
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={() => setShowPassword(!showPassword)}>
                    {showPassword ? (
                      <VisibilityOff sx={{ color: "#073763" }} />
                    ) : (
                      <Visibility sx={{ color: "#073763" }} />
                    )}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />

          <Button
            fullWidth
            type="submit"
            sx={{
              mt: 3,
              py: 1.5,
              fontWeight: 600,
              background: "linear-gradient(90deg, #2196f3, #64b5f6)",
              color: "#fff",
              transition: "0.3s",
              "&:hover": {
                background: "linear-gradient(90deg, #1976d2, #4dabf5)",
              },
            }}
          >
            Login
          </Button>
        </form>

        <Typography mt={2} sx={{ color: "#073763", textAlign: "center" }}>
          No account?{" "}
          <Link to="/signup" style={{ color: "#064f9c", textDecoration: "underline" }}>
            Signup
          </Link>
        </Typography>
      </Paper>
    </Box>
  );
};

export default Login;
