import { Card, CardContent, Typography, IconButton, Box } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";

const TaskCard = ({ task, onDelete, onEdit, isNew }) => {
  return (
    <Card
      sx={{
        mb: 2,
        borderRadius: 3,
        background: isNew
          ? "linear-gradient(135deg, #E3F2FD, #BBDEFB)"
          : "#ffffff",
        border: isNew ? "2px solid #1976d2" : "1px solid #eee",
        boxShadow: isNew
          ? "0 8px 24px rgba(25,118,210,0.3)"
          : "0 4px 12px rgba(0,0,0,0.08)",
        transition: "0.3s",
        "&:hover": {
          transform: "translateY(-3px)",
          boxShadow: "0 10px 28px rgba(0,0,0,0.15)",
        },
      }}
    >
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Box>
            <Typography variant="h6" sx={{ fontWeight: 600 }}>
              {task.title}
            </Typography>
            <Typography variant="body2" sx={{ color: "text.secondary" }}>
              {task.description}
            </Typography>
          </Box>

          <Box>
            <IconButton color="primary" onClick={() => onEdit(task)}>
              <EditIcon />
            </IconButton>
            <IconButton color="error" onClick={() => onDelete(task._id)}>
              <DeleteIcon />
            </IconButton>
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
};

export default TaskCard;
