import { AppBar, Toolbar, Typography, Avatar, Menu, MenuItem, IconButton } from "@mui/material";
import { useContext, useState } from "react";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);

  if (!user) return null;

  const handleMenuOpen = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);

  return (
    <AppBar 
      position="static" 
      sx={{
        background: 'linear-gradient(90deg, #4dabf5, #1976d2)', // subtle blue gradient
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)', // soft shadow
        borderBottom: '1px solid rgba(255,255,255,0.2)',
      }}
    >
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 600 }}>
          Knit Finance Dashboard
        </Typography>

        {/* Avatar with first letter */}
        <IconButton onClick={handleMenuOpen} sx={{ p: 0, mr: 2 }}>
          <Avatar 
            sx={{ 
              bgcolor: '#90caf9', // soft aesthetic blue
              width: 36, 
              height: 36,
              fontWeight: 600,
              color: '#fff'
            }}
          >
            {user.name[0].toUpperCase()}
          </Avatar>
        </IconButton>

        <Menu
          anchorEl={anchorEl}
          open={open}
          onClose={handleMenuClose}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
          transformOrigin={{ vertical: 'top', horizontal: 'right' }}
        >
          <MenuItem>{user.name}</MenuItem>
          <MenuItem onClick={() => { logout(); handleMenuClose(); }}>
            Logout
          </MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
