import { useEffect, useState, useContext } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  Grid,
} from "@mui/material";
import API from "../services/api";
import TaskCard from "../components/TaskCard";
import { AuthContext } from "../context/AuthContext";

const Dashboard = () => {
  const { user } = useContext(AuthContext);

  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [search, setSearch] = useState("");
  const [editId, setEditId] = useState(null);

  const fetchTasks = async () => {
    try {
      const res = await API.get("/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const saveTask = async () => {
    if (!title) return;

    try {
      if (editId) {
        await API.put(`/tasks/${editId}`, { title, description });
      } else {
        await API.post("/tasks", { title, description });
      }

      setTitle("");
      setDescription("");
      setEditId(null);
      fetchTasks();
    } catch (err) {
      console.error(err);
    }
  };

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box sx={{ minHeight: "100vh", px: 4, pt: 4 }}>

      {/* üåü Welcome */}
      <Typography
        variant="h4"
        sx={{
          textAlign: "center",
          fontWeight: 700,
          mb: 1,
          color: "#0d3c61",
        }}
      >
        Welcome, {user?.name} üéâ
      </Typography>

      <Typography
        sx={{
          textAlign: "center",
          mb: 4,
          color: "#355f7a",
        }}
      >
        Manage your tasks efficiently ‚ú®
      </Typography>

      {/* üßä Add / Edit Task Box */}
      <Paper
        elevation={0}
        sx={{
          maxWidth: 950,
          mx: "auto",
          p: 4,
          mb: 4,
          minHeight: 220,
          borderRadius: 4,
          background: "rgba(255,255,255,0.55)", // SAME background
          backdropFilter: "blur(12px)",
          boxShadow: "0 12px 30px rgba(0,0,0,0.12)",
        }}
      >
        <Grid container columnSpacing={6} rowSpacing={3}>
          {/* Task Title - WIDE */}
          <Grid item xs={12} md={8}>
            <TextField
              fullWidth
              label="Task Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </Grid>

          {/* Description - SMALL */}
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              label="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Grid>
        </Grid>

        <Button
          fullWidth
          onClick={saveTask}
          sx={{
            mt: 4,
            py: 1.7,
            fontSize: "1rem",
            fontWeight: 600,
            borderRadius: 3,
            background:
              "linear-gradient(135deg, #2196f3, #64b5f6)",
            color: "#fff",
            "&:hover": {
              background:
                "linear-gradient(135deg, #1e88e5, #42a5f5)",
            },
          }}
        >
          {editId ? "Update Task" : "Add Task"}
        </Button>
      </Paper>

      {/* üîç Search */}
      <Box sx={{ maxWidth: 500, mx: "auto", mb: 4 }}>
        <TextField
          fullWidth
          label="Search Tasks"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </Box>

      {/* üìã Task List */}
      <Grid container spacing={3}>
        {filteredTasks.map((task) => (
          <Grid item xs={12} sm={6} md={4} key={task._id}>
            <TaskCard task={task} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Dashboard;
