import { Card, CardContent, Typography, IconButton, TextField, Box } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import { useState } from "react";

const TaskCard = ({ task, onDelete, onUpdate }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [newTitle, setNewTitle] = useState(task.title);

  const handleUpdate = () => {
    onUpdate(task._id, newTitle);
    setIsEditing(false);
  };

  return (
    <Card sx={{ mb: 2 }}>
      <CardContent>
        {isEditing ? (
          <Box sx={{ display: "flex", gap: 1 }}>
            <TextField value={newTitle} onChange={(e) => setNewTitle(e.target.value)} size="small" />
            <IconButton onClick={handleUpdate}><EditIcon /></IconButton>
          </Box>
        ) : (
          <Typography variant="h6">{task.title}</Typography>
        )}
        <Typography variant="body2">{task.description}</Typography>
        <Box>
          <IconButton onClick={() => onDelete(task._id)}><DeleteIcon /></IconButton>
          {!isEditing && <IconButton onClick={() => setIsEditing(true)}><EditIcon /></IconButton>}
        </Box>
      </CardContent>
    </Card>
  );
};

export default TaskCard;
