import { useEffect, useState } from "react";
import { Box, Typography, TextField, Button, Paper, Grid } from "@mui/material";
import API from "../services/api";
import TaskCard from "../components/TaskCard";

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [search, setSearch] = useState("");
  const [editId, setEditId] = useState(null);

  // Fetch tasks
  const fetchTasks = async () => {
    try {
      const res = await API.get("/tasks");
      setTasks(res.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Add or Edit Task
  const saveTask = async () => {
    if (!title) return;

    try {
      if (editId) {
        await API.put(`/tasks/${editId}`, { title, description });
        setEditId(null);
      } else {
        await API.post("/tasks", { title, description });
      }

      setTitle("");
      setDescription("");
      fetchTasks();
    } catch (error) {
      console.error("Error saving task:", error);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      await API.delete(`/tasks/${id}`);
      fetchTasks();
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  // Edit task
  const editTask = (task) => {
    setTitle(task.title);
    setDescription(task.description);
    setEditId(task._id);
  };

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundColor: "#98C6E8",
        p: 3,
      }}
    >
      <Typography variant="h4" sx={{ mb: 3, color: "#073763", fontWeight: 600, textAlign: "center" }}>
        Dashboard
      </Typography>

      {/* Add / Edit Task */}
      <Paper
        elevation={6}
        sx={{
          backdropFilter: "blur(12px)",
          background: "rgba(255,255,255,0.25)",
          borderRadius: 3,
          p: 3,
          mb: 3,
        }}
      >
        <TextField
          fullWidth
          label="Task Title"
          margin="normal"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          InputLabelProps={{ style: { color: "#073763" } }}
          InputProps={{ style: { color: "#073763" } }}
        />
        <TextField
          fullWidth
          label="Description"
          margin="normal"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          InputLabelProps={{ style: { color: "#073763" } }}
          InputProps={{ style: { color: "#073763" } }}
        />
        <Button
          fullWidth
          onClick={saveTask}
          sx={{
            mt: 2,
            py: 1.5,
            fontWeight: 600,
            background: "linear-gradient(90deg, #2196f3, #64b5f6)",
            color: "#fff",
            transition: "0.3s",
            "&:hover": {
              background: "linear-gradient(90deg, #1976d2, #4dabf5)",
            },
          }}
        >
          {editId ? "Save Task" : "Add Task"}
        </Button>
      </Paper>

      {/* Search */}
      <TextField
        fullWidth
        label="Search Tasks"
        margin="normal"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        InputLabelProps={{ style: { color: "#073763" } }}
        InputProps={{ style: { color: "#073763" } }}
        sx={{ mb: 3 }}
      />

      {/* Task List */}
      <Grid container spacing={2}>
        {filteredTasks.map((task) => (
          <Grid item xs={12} sm={6} md={4} key={task._id}>
            <TaskCard task={task} onDelete={deleteTask} onEdit={editTask} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Dashboard;
