import { AppBar, Toolbar, Typography, Avatar, Button, Box } from "@mui/material";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  if (!user) return null;

  return (
    <AppBar
      position="static"
      sx={{
        background: 'linear-gradient(90deg, #4dabf5, #1976d2)', // linear gradient
        boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
        borderBottom: '1px solid rgba(255,255,255,0.2)',
      }}
    >
      <Toolbar>
        {/* Dashboard title */}
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 600 }}>
          Knit Finance Dashboard
        </Typography>

        {/* Profile avatar + logout inline */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Avatar
            sx={{
              bgcolor: '#90caf9', // aesthetic soft blue
              width: 40,
              height: 40,
              fontWeight: 600,
              color: '#fff',
            }}
          >
            {user.name[0].toUpperCase()}
          </Avatar>
          <Box>
            <Typography variant="subtitle2" sx={{ fontWeight: 600, color: '#fff' }}>
              {user.name}
            </Typography>
            <Typography variant="body2" sx={{ color: '#e0f7fa' }}>
              {user.email}
            </Typography>
          </Box>
          <Button
            variant="contained"
            color="secondary"
            size="small"
            sx={{ ml: 2 }}
            onClick={logout}
          >
            Logout
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
