import { useEffect, useState } from "react";
import { Container, Typography, TextField, Button, Box } from "@mui/material";
import API from "../services/api";
import TaskCard from "../components/TaskCard";

const Dashboard = () => {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [search, setSearch] = useState("");

  const fetchTasks = async () => {
    const res = await API.get("/tasks");
    setTasks(res.data);
  };

  useEffect(() => { fetchTasks(); }, []);

  const addTask = async () => {
    if (!title) return;
    await API.post("/tasks", { title, description });
    setTitle(""); setDescription("");
    fetchTasks();
  };

  const deleteTask = async (id) => { await API.delete(`/tasks/${id}`); fetchTasks(); };
  const updateTask = async (id, newTitle) => { await API.put(`/tasks/${id}`, { title: newTitle }); fetchTasks(); };

  const filteredTasks = tasks.filter(task => task.title.toLowerCase().includes(search.toLowerCase()));

  return (
    <Container maxWidth="md">
      <Typography variant="h4" mt={4} mb={2}>Dashboard</Typography>

      <Box mb={3}>
        <TextField fullWidth label="Task Title" margin="normal" value={title} onChange={(e) => setTitle(e.target.value)} />
        <TextField fullWidth label="Description" margin="normal" value={description} onChange={(e) => setDescription(e.target.value)} />
        <Button variant="contained" onClick={addTask}>Add Task</Button>
      </Box>

      <TextField fullWidth label="Search Tasks" margin="normal" onChange={(e) => setSearch(e.target.value)} />

      <Box mt={2}>
        {filteredTasks.map(task => (
          <TaskCard key={task._id} task={task} onDelete={deleteTask} onUpdate={updateTask} />
        ))}
      </Box>
    </Container>
  );
};

export default Dashboard;
